import React from "react";
import { useState, useEffect } from "react";
import MeetupList from "../components/meetups/MeetupList";

function AllMeetupsPage() {

  {/* This use state hook is true when the fetch hasnt returned the promise yet aka is still loading*/}
  const [isLoading, setIsLoading] = useState(true);
  {/* This use state hook is initiated with and empty array which will eventually hold an array of data from fetch */}
  const [loadedMeetups, setLoadedMeetups] = useState([]);

  {}
  useEffect(() => {
    setIsLoading(true);
    fetch(
      "https://react-getting-started-19fd9-default-rtdb.firebaseio.com/meetups.json"
    )
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        const meetups = [];

        for (const key in data) {
          const meetup = {
            id: key,
            ...data[key],
          };
          meetups.push(meetup);
        }

        setIsLoading(false);
        setLoadedMeetups(meetups);
      });
  }, []);

  if (isLoading) {
    return (
      <section>
        <p>Loading...</p>
      </section>
    );
  }

  return (
    <section>
      <h1>All Meetups</h1>
      {/* MeetupList is a function that takes an array of objects aka (data) passed through a prop called 'meetups' and will map through all the object variables
            and get it formatted by another component */}
      <MeetupList meetups={loadedMeetups} />
    </section>
  );
}

export default AllMeetupsPage;
